import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Header from '../../component/header/Header';
import Footer from '../../component/footer/Footer';
import './Login.css';

function Login() {
  const [phoneNumber, setPhoneNumber] = useState('');
  const [isValid, setIsValid] = useState(true);
  const [click, setClick] = useState(false);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handlePhoneNumberChange = (e) => {
    let value = e.target.value;
    // Filter out non-numeric characters
    value = value.replace(/\D/g, '');
    // Limit to exactly 10 digits
    value = value.slice(0, 10);
    setPhoneNumber(value);
    setIsValid(value.length === 10);
  };

  const handleSubmit = async () => {
    if (isValid) {
      setClick(true);
      try {
        const response = await fetch('https://gxppcdmn7h.execute-api.ap-south-1.amazonaws.com/authgw/sendotp', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({ phoneNumber }),
        });

        const data = await response.json();
        if (response.ok) {
          // Navigate to the next page (replace 'NextPage' with your actual route)
          navigate('/nextpage', { state: { phoneNumber } });
        } else {
          setError(data.message || 'An error occurred. Please try again.');
        }
      } catch (error) {
        setError('An error occurred. Please try again.');
      } finally {
        setClick(false);
      }
    }
  };

  return (
    <>
      <Header />
      <div className="all-text">
        <div className="margin-top">
          <div className="main-text">Good Morning,</div>
          <div className="second-text">What are you going to learn today?</div>
          <div>
            <div>
              <label htmlFor="phoneNumber" className="third-text">Login to start</label>
            </div>
            <div className='conten-display'>
              <div className="inputContainer">
                <input
                  type="text"
                  id="phoneNumber"
                  value={phoneNumber}
                  onChange={handlePhoneNumberChange}
                  placeholder="Enter 10-digit phone number"
                  className={`inputField ${isValid ? '' : 'error'}`}
                />
                {!isValid && <p className="errorText">Please enter a 10-digit phone number.</p>}
                {error && <p className="errorText">{error}</p>}
              </div>
              <div>
                <button onClick={handleSubmit} className="clickable-buttons" disabled={click}>
                  <svg className='adjust-img'
                    xmlns="http://www.w3.org/2000/svg"
                    width="20"
                    height="20"
                    color='black'
                    fill="currentColor"
                    className="bi bi-chevron-right"
                    viewBox="0 0 30 20"
                  >
                    <path
                      fillRule="evenodd"
                      d="M4.646 1.646a.5.5 0 0 1 .708 0l6 6a.5.5 0 0 1 0 .708l-6 6a.5.5 0 0 1-.708-.708L10.293 8 4.646 2.354a.5.5 0 0 1 0-.708"
                    />
                  </svg>
                  <i className="fa-solid fa-angle-right icon_right1"></i>
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>
      <Footer />
    </>
  );
}

export default Login;
