import React, { useState, useEffect, useRef } from "react";
import { useNavigate, useLocation } from "react-router-dom";
import Cookies from "js-cookie";
import { toast } from "react-toastify";
import './VerifyOtp.css';
import Header from "../../component/header/Header";
import Footer from "../../component/footer/Footer";

const VerifyOtp = () => {
  const [isLoading, setIsLoading] = useState(false);
  const [otpValues, setOtpValues] = useState(["", "", "", "", "", ""]);
  const navigate = useNavigate();
  const [password, setPassword] = useState("");
  const [enterPassword, setEnterPassword] = useState(false);
  const location = useLocation();
  const inputRefs = useRef([...Array(6)].map(() => React.createRef()));
  const phoneNumber = location.state?.phoneNumber;

  useEffect(() => {
    if (inputRefs.current[0]?.current) {
      inputRefs.current[0].current.focus();
    }
  }, []);

  if (!phoneNumber) {
    return <p>No phone number provided</p>;
  }

  const showToast = (message, type, toastId, fontSize, backgroundColor) => {
    const toastConfig = {
      toastId: toastId,
      style: {
        fontSize: fontSize || "14px",
        color: "white",
        backgroundColor: backgroundColor || "#51a351",
      },
      className: "custom-toast",
    };

    if (!toast.isActive(toastId)) {
      toast[type](message, toastConfig);
    }
  };

  const handleOtpChange = (index, value) => {
    const numericValue = value.replace(/\D/g, "");
    const newOtpValues = [...otpValues];
    newOtpValues[index] = numericValue;

    if (numericValue.length === 1 && index < inputRefs.current.length - 1) {
      inputRefs.current[index + 1].current.focus();
    }

    setOtpValues(newOtpValues);
  };

  const handleOtpKeyDown = (e, index) => {
    if (e.key === "Backspace" && otpValues[index] === "" && index > 0) {
      e.preventDefault();
      inputRefs.current[index - 1].current.focus();
    }
  };

  const handleKeyPress = (event) => {
    if (event.key === "Enter") {
      event.preventDefault();
      handleOtpSubmit();
    }
  };

  const handleOtpSubmit = async () => {
    setIsLoading(true);

    const phoneNumber = location.state?.phoneNumber;
    let payload;

    if (enterPassword) {
      payload = {
        phoneNumber: phoneNumber,
        password: password,
      };
    } else {
      const otp = otpValues.join("");
      const otpAsNumber = parseInt(otp, 10);

      if (otpValues.some(value => !value.trim())) {
        setIsLoading(false);
        showToast("Enter the OTP sent!", "error", "otp-error-toast", "14px", "#FF4D4F");
        return;
      }

      if (isNaN(otpAsNumber)) {
        showToast("Invalid OTP Format", "error", "otp-error-toast", "17px", "#FF0000");
        setIsLoading(false);
        return;
      }

      payload = {
        otp: otpAsNumber,
        phoneNumber: phoneNumber,
      };
    }

    try {
      const response = await fetch("https://4r4iwhot12.execute-api.ap-south-1.amazonaws.com/auth/auth/validateOtp", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(payload),
      });

      if (response.ok) {
        setIsLoading(false);
        showToast("User Login Successfully!", "success", "otp-success-toast", "", "#008000");
        let resp = await response.json();
        localStorage.setItem("token", resp.token);
        Cookies.set("token", resp.token, { expires: 7, domain: "localhost", path: "/" });
        setTimeout(() => {
          navigate("/select-group", { state: { phoneNumber } });
        }, 3000);
      } else {
        setIsLoading(false);
        showToast(enterPassword ? "Incorrect credentials! Please try again." : "Incorrect OTP! Please try again.", "error", "otp-error-toast", "", "#FF0000");
      }
    } catch (error) {
      showToast("An error occurred", "error", "otp-error-toast", "", "#FF4D4F");
      window.location.reload();
    }
  };

  return (
    <>
      <div className='container-fluid'>
        <div className="row">
          <div className="col-lg-2 col-sm-12 col-md-4">
            <Header />
          </div>
        </div>
        <div className="titletext row ml-3">
          <div className="col-lg-1 col-sm-12 col-md-12 ml-5"></div>
          <div className="col-lg-6 col-sm-12 col-md-12">
            <h1 className="title1">Good Morning,</h1>
            <h2 className="Text1">What are you going to learn today?</h2>
            <p className="text2 pt-4">Verify your OTP</p>
            <div className="otp-input-container">
              {otpValues.map((value, index) => (
                <input
                  key={index}
                  type="text"
                  className="inputField1"
                  value={value}
                  onChange={(e) => handleOtpChange(index, e.target.value)}
                  onKeyDown={(e) => handleOtpKeyDown(e, index)}
                  ref={inputRefs.current[index]}
                  maxLength={1}
                  onKeyPress={handleKeyPress}
                />
              ))}
            </div>
            <button onClick={handleOtpSubmit} className="otpbutton1 pt-2" disabled={isLoading}>
              <div className="verify_icon m-0 p-0">
                <i className="fa-solid fa-angle-right icon_right m-0 p-0"></i>
              </div>
            </button>
            {error && <p className="error">{error}</p>}
            <p className="third5">change Login details</p>
          </div>
          <div className="col-5"></div>
        </div>
      </div>
      <div>
        <div>
          <Footer />
        </div>
      </div>
    </>
  );
}

export default VerifyOtp;
