import React, { useState, useEffect } from "react";
import './VerifyOtp.css';
import Header from "../../component/header/Header";
import Footer from "../../component/footer/Footer";

function VerifyOtp() {
  const [otp, setOtp] = useState("");
  const [errorMessage, setErrorMessage] = useState(null);

  useEffect(() => {
    // Fetch the API URL from environment variables securely
    const apiUrl = process.env.REACT_APP_VERIFY_OTP_API_URL;

    const handleSubmit = async () => {
      if (otp.length === 6) {
        try {
          const response = await fetch(apiUrl, {
            method: 'POST', // Assuming POST for OTP validation
            headers: {
              'Content-Type': 'application/json', // Adjust if needed
            },
            body: JSON.stringify({ otp }), // Assuming OTP is sent in the body
          });

          if (!response.ok) {
            throw new Error(`API request failed with status ${response.status}`);
          }

          const data = await response.json();
          console.log("OTP verified:", data); // Handle successful validation here
        } catch (error) {
          setErrorMessage(error.message);
        }
      } else {
        console.log("Invalid OTP");
      }
    };

    return () => {
      // Cleanup function (optional)
    };
  }, [otp, apiUrl]);

  const handleChange = (e) => {
    const value = e.target.value;

    if (!isNaN(value) && value.length <= 6) {
      setOtp(value);
      setErrorMessage(null); // Clear error message on input change
    }
  };

  return (
    <>
      <div className="container-fluid">
        <div className="row">
          <div className="col-lg-2 col-sm-12 col-md-4 ">
            <Header />
          </div>
        </div>
        <div className="titletext row ml-3">
          <div className="col-lg-1 col-sm-12 col-md-12 ml-5"></div>
          <div className="col-lg-6 col-sm-12 col-md-12">
            <h1 className="title1">Good Morning,</h1>
            <h2 className="Text1">What are you going to learn today?</h2>
            <p className="text2 pt-4">Verify your OTP</p>
            <input
              type="number"
              className="inputField1"
              value={otp}
              onChange={handleChange}
              maxLength={6}
              minLength={6}
            />
            <button onClick={handleSubmit} className="otpbutton1 pt-2">
              <div className="verify_icon m-0 p-0">
                <i className="fa-solid fa-angle-right icon_right m-0 p-0 Â "></i>
              </div>
            </button>
            <p className="third5">change Login details</p>
            {errorMessage && <p className="error-message">{errorMessage}</p>}
          </div>
          <div className="col-5"></div>
        </div>
      </div>
      <div>
        <Footer />
      </div>
    </>
  );
}

export default VerifyOtp;
