import React, { useState, useEffect } from "react";
import { useNavigate, useLocation } from "react-router-dom";
import { toast, ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import './VerifyOtp.css';
import Header from "../../component/header/Header";
import Footer from "../../component/footer/Footer";
import { FaAngleRight } from 'react-icons/fa'; // Importing the Right Angle icon from react-icons/fa

function VerifyOtp() {
  const [otp, setOtp] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const location = useLocation();
  const navigate = useNavigate();
  const phoneNumber = location.state?.phoneNumber;

  useEffect(() => {
    if (!phoneNumber) {
      navigate("/login"); // Redirect to login if no phone number is provided
    }
  }, [phoneNumber, navigate]);

  const handleChange = (e) => {
    const value = e.target.value;
    if (!isNaN(value) && value.length <= 6) {
      setOtp(value);
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      handleSubmit();
    }
  };

  const showToast = (message, type, backgroundColor) => {
    toast[type](message, {
      style: {
        backgroundColor: backgroundColor,
        color: 'white',
        minHeight: '50px',
        fontSize: '16px',
      },
      className: 'custom-toast',
    });
  };

  const handleSubmit = async () => {
    // Your submit logic
  };

  return (
    <>
      <div className='container-fluid'>
        <div className="row">
          <div className="col-lg-2 col-sm-12 col-md-4 ">
            <Header />
          </div>
        </div>
        <div className="titletext row ml-3">
          <div className="col-lg-1 col-sm-12 col-md-12 ml-5"></div>
          <div className="col-lg-6 col-sm-12 col-md-12">
            <h1 className="title1">Good Morning,</h1>
            <h2 className="Text1">What are you going to learn today?</h2>
            <p className="text2 pt-4">Verify your OTP</p>
            <div className="otp-input-container">
              <input
                type="password"
                className="inputField1"
                value={otp}
                onChange={handleChange}
                maxLength={6}
                minLength={6}
                onKeyPress={handleKeyPress} // Add event listener for Enter key press
              />
              <FaAngleRight className="submit-icon" onClick={handleSubmit} />
            </div>
            <p className="third5">change Login details</p>
          </div>
          <div className="col-5"></div>
        </div>
      </div>
      <div>
        <div>
          <Footer />
        </div>
      </div>
      <ToastContainer
        position="top-right"
        autoClose={2000}
        hideProgressBar={false}
        newestOnTop={false}
        closeOnClick
        rtl={false}
        pauseOnFocusLoss
        draggable
        pauseOnHover
        toastStyle={{ background: "#FF0000", color: "white", minHeight: "50px" }}
      />
    </>
  );
}

export default VerifyOtp;
